package org.slerpio.service.profile.test;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.slerp.core.Domain;
import org.slerp.core.business.BusinessTransaction;
import org.springframework.beans.factory.annotation.Autowired;
import org.junit.runner.RunWith;
import org.springframework.test.context.junit4.SpringJUnit4ClassRunner;
import org.springframework.test.context.ContextConfiguration;
import org.springframework.test.context.TestExecutionListeners;
import org.springframework.test.context.support.DirtiesContextTestExecutionListener;
import org.springframework.test.context.transaction.TransactionalTestExecutionListener;
import org.springframework.test.context.support.DependencyInjectionTestExecutionListener;
import org.springframework.test.annotation.Rollback;
import org.springframework.test.context.junit4.AbstractTransactionalJUnit4SpringContextTests;
import org.junit.Before;
import org.junit.Test;
import org.assertj.core.api.Assertions;

@RunWith(SpringJUnit4ClassRunner.class)
@ContextConfiguration(locations = "classpath:applicationContext.xml")
@TestExecutionListeners(listeners = {DirtiesContextTestExecutionListener.class,
		TransactionalTestExecutionListener.class,
		DependencyInjectionTestExecutionListener.class}, inheritListeners = false)
@Rollback
public class EditPhotoProfileTest
		extends
			AbstractTransactionalJUnit4SpringContextTests {

	static private Logger log = LoggerFactory
			.getLogger(EditPhotoProfileTest.class);
	@Autowired
	BusinessTransaction editPhotoProfile;

	@Before
	public void prepare() {
		executeSqlScript(
				"classpath:org/slerpio/service/profile/test/EditPhotoProfileTest.sql",
				false);
	}

	@Test
	public void testSuccess() {
		String picture = "data:image/png;base64,";
		String username = "kiditz";
		Domain profileDomain = new Domain();
		profileDomain.put("picture", picture);
		profileDomain.put("username", username);
		Domain outputProfile = editPhotoProfile.handle(profileDomain);
		log.info("Result Test {}", outputProfile);
		Assertions.assertThat(profileDomain.get("picture")).isEqualTo(picture);
		Assertions.assertThat(profileDomain.get("username"))
				.isEqualTo(username);
	}
}